//@ts-nocheck
"use client";
import { motion } from "framer-motion";
import React from "react";

interface FeatureProps {
  icon: React.ReactNode;
  text: string;
  isActive?: boolean;
}

interface TagProps {
  icon: React.ReactNode;
  text: string;
  isActive: boolean;
  onClick?: () => void;
}

const Feature: React.FC<FeatureProps> = ({ icon, text, isActive = false }) => (
  <div className="flex items-center gap-3">
    <div
      className={`size-14 rounded-[16px] vyvo-button ${
        isActive ? "vyvo-tech-bg-banner-tag" : "bg-[#FCFCFE]"
      } shrink-0 grid place-content-center`}
    >
      {icon}
    </div>
    <span className="font-nb text-[16px] leading-[20px] tracking-[-0.03em] vyvo-tech-gradient-text max-w-[228px]">
      {text}
    </span>
  </div>
);

const Tag: React.FC<TagProps> = ({ icon, text, isActive, onClick }) => (
  <div className="flex items-center gap-3 cursor-pointer" onClick={onClick}>
    <div
      className={`size-12 rounded-full ${
        isActive ? "vyvo-tech-bg-banner-tag" : "bg-[#FCFCFE]"
      } shrink-0 grid place-content-center`}
    >
      {React.cloneElement(icon, {
        className: `size-6 ${isActive ? "stroke-[#fff]" : "stroke-[#2A5FDD]"}`, // Dynamically inject className
      })}
    </div>
    <span className="font-nb text-[16px] leading-[20px] tracking-[-0.03em] vyvo-tech-gradient-text">
      {text}
    </span>
  </div>
);

const BiosenseRing: React.FC = () => {
  const [activeTag, setActiveTag] = React.useState("card1");

  const tags = [
    { id: "card1", icon: <Icon1 />, text: "Biosense Ring" },
    { id: "card2", icon: <Icon2 />, text: "BioSense Band" },
    { id: "card3", icon: <Icon3 />, text: "BioSense Watch" },
  ];

  // const features = [
  //   { icon: <HeartIcon />, text: "Advanced Sensors for sleep, heart rate" },
  //   { icon: <MonitoringIcon />, text: "Vital Sign Monitoring" },
  //   { icon: <WeeklyHealth />, text: "Weekly Health Index" },
  //   { icon: <BloodPressureIcon />, text: "Blood Pressure and Oxygen Tracking" },
  // ];

  const handleTagClick = (id: string) => {
    setActiveTag(id);
  };

  return (
    <section className="min-h-screen bg-white w-full py-20 lg:block hidden ">
      <div className="max-w-[1280px] mx-auto w-full flex flex-col gap-[142px]">
        <h2 className="vyvo-tech-gradient-text font-light text-center font-nb text-[64px] leading-[68px]">
          BioSense Ring
        </h2>

        <div className="w-full flex justify-start gap-[100px]">
          {/* Tags */}
          <div className="flex flex-col gap-6">
            {tags.map(({ id, icon, text }) => (
              <Tag
                key={id}
                icon={icon}
                text={text}
                isActive={activeTag === id}
                onClick={() => handleTagClick(id)}
              />
            ))}
          </div>

          {/* Content Section */}
          <div className="flex flex-col justify-center items-center gap-10 mt-[-10%]">
            <img
              src={`/vyvo-tech/${activeTag}.png`}
              alt={activeTag}
              className="w-[810px] h-[500px] object-contain"
            />
            <div className="flex flex-col gap-[30px] items-center">
              <p className="max-w-[744px] w-full font-nb text-[20px] leading-[26px] text-black text-center">
                Watching over your well-being has never fit this well. The
                BioSense Ring is a lightweight, discreet companion that
                seamlessly tracks key metrics, delivering meaningful health
                insights to help you optimize your well-being anytime, anywhere.
              </p>
              <button className="py-[15px] bg-[#77A9E829] hover:bg-transparent border border-transparent hover:border-[#77A9E829] transition-all duration-300 ease-in-out vyvo-button w-full max-w-[160px] rounded-[16px]">
                <span className="text-[16px] leading-[18px] text-black tracking-[-0.01em] font-normal">
                  Learn more
                </span>
              </button>
            </div>
          </div>

          {/* Features */}
          {/* <div className="flex flex-col gap-6">
            {features.map(({ icon, text }, index) => (
              <Feature key={index} icon={icon} text={text} />
            ))}
          </div> */}
        </div>
      </div>
    </section>
  );
};

export default BiosenseRing;

